package week1.day2;

public class IsPrime {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 79;
		
		for(int i=2;i<a;i++)
		{
			if(a%i ==0)
			{
				System.out.println("It is not a Prime Number");
			break;
			}
			else
			{
				System.out.println("It is a Prime Number");
				break;
				
			}

	}
	}
}
